import React, { useState } from 'react';
import Modal from './Modal';
import { 
  Mail, 
  Lock, 
  User, 
  Building, 
  GraduationCap,
  Eye,
  EyeOff,
  CheckCircle,
  AlertCircle
} from 'lucide-react';

const AuthModal = ({ isOpen, onClose, onAuth, defaultMode = 'login', defaultRole = null }) => {
  const [mode, setMode] = useState(defaultMode); // 'login' or 'register'
  const [role, setRole] = useState(defaultRole || 'freelancer'); // 'freelancer' or 'client'
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    university: '',
    major: '',
    companyName: '',
    phone: ''
  });
  const [errors, setErrors] = useState({});

  const handleSubmit = (e) => {
    e.preventDefault();
    
    const newErrors = {};
    
    if (mode === 'register') {
      if (!formData.name) newErrors.name = 'Name is required';
      if (!formData.email) newErrors.email = 'Email is required';
      if (!formData.password) newErrors.password = 'Password is required';
      if (formData.password.length < 6) newErrors.password = 'Password must be at least 6 characters';
      if (!formData.confirmPassword) newErrors.confirmPassword = 'Please confirm your password';
      if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Passwords do not match';
      
      if (role === 'freelancer') {
        if (!formData.university) newErrors.university = 'University is required';
        if (!formData.major) newErrors.major = 'Major is required';
      } else {
        if (!formData.companyName) newErrors.companyName = 'Company name is required';
        if (!formData.phone) newErrors.phone = 'Phone number is required';
      }
    } else {
      if (!formData.email) newErrors.email = 'Email is required';
      if (!formData.password) newErrors.password = 'Password is required';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    // Simulate authentication
    onAuth({
      mode,
      role,
      ...formData
    });
    
    onClose();
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: null }));
    }
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} title={mode === 'login' ? 'Sign In' : 'Create Account'} size="medium">
      <form onSubmit={handleSubmit}>
        <div style={{ display: 'grid', gap: 'var(--spacing-lg)' }}>
          {/* Mode Toggle */}
          <div className="flex gap-sm" style={{ justifyContent: 'center', marginBottom: 'var(--spacing-lg)' }}>
            <button
              type="button"
              onClick={() => setMode('login')}
              className="button"
              style={{
                backgroundColor: mode === 'login' ? 'var(--colors-neutral-dark)' : 'var(--colors-background-surface)',
                color: mode === 'login' ? 'var(--colors-text-onDark)' : 'var(--colors-text-primary)'
              }}
            >
              Sign In
            </button>
            <button
              type="button"
              onClick={() => setMode('register')}
              className="button"
              style={{
                backgroundColor: mode === 'register' ? 'var(--colors-neutral-dark)' : 'var(--colors-background-surface)',
                color: mode === 'register' ? 'var(--colors-text-onDark)' : 'var(--colors-text-primary)'
              }}
            >
              Sign Up
            </button>
          </div>

          {/* Role Selection (only for register) */}
          {mode === 'register' && (
            <div>
              <label style={{ 
                display: 'block', 
                fontSize: 'var(--body-font-size)', 
                fontWeight: '600', 
                marginBottom: 'var(--spacing-md)',
                color: 'var(--colors-text-primary)'
              }}>
                I am a:
              </label>
              <div className="grid grid-2" style={{ gap: 'var(--spacing-md)' }}>
                <button
                  type="button"
                  onClick={() => setRole('freelancer')}
                  className="card"
                  style={{
                    border: `2px solid ${role === 'freelancer' ? 'var(--colors-primary-lime)' : 'var(--colors-border-subtle)'}`,
                    backgroundColor: role === 'freelancer' ? 'var(--colors-primary-lime)' : 'var(--colors-background-default)',
                    cursor: 'pointer',
                    textAlign: 'center'
                  }}
                >
                  <GraduationCap size={32} style={{ marginBottom: 'var(--spacing-sm)' }} />
                  <h4 style={{ fontSize: 'var(--body-font-size)', fontWeight: '600' }}>Student Freelancer</h4>
                  <p style={{ fontSize: 'var(--metadata-font-size)', color: 'var(--colors-text-secondary)' }}>
                    Find gigs and build your portfolio
                  </p>
                </button>
                <button
                  type="button"
                  onClick={() => setRole('client')}
                  className="card"
                  style={{
                    border: `2px solid ${role === 'client' ? 'var(--colors-secondary-purple)' : 'var(--colors-border-subtle)'}`,
                    backgroundColor: role === 'client' ? 'var(--colors-secondary-purple)' : 'var(--colors-background-default)',
                    cursor: 'pointer',
                    textAlign: 'center'
                  }}
                >
                  <Building size={32} style={{ marginBottom: 'var(--spacing-sm)' }} />
                  <h4 style={{ fontSize: 'var(--body-font-size)', fontWeight: '600' }}>Client</h4>
                  <p style={{ fontSize: 'var(--metadata-font-size)', color: 'var(--colors-text-secondary)' }}>
                    Post projects and hire students
                  </p>
                </button>
              </div>
            </div>
          )}

          {/* Name (register only) */}
          {mode === 'register' && (
            <div>
              <label style={{ 
                display: 'block', 
                fontSize: 'var(--body-font-size)', 
                fontWeight: '600', 
                marginBottom: 'var(--spacing-sm)',
                color: 'var(--colors-text-primary)'
              }}>
                Full Name *
              </label>
              <div style={{ position: 'relative' }}>
                <User 
                  size={20} 
                  style={{
                    position: 'absolute',
                    left: 'var(--spacing-md)',
                    top: '50%',
                    transform: 'translateY(-50%)',
                    color: 'var(--colors-text-secondary)'
                  }} 
                />
                <input
                  type="text"
                  value={formData.name}
                  onChange={(e) => handleInputChange('name', e.target.value)}
                  placeholder="Enter your full name"
                  style={{
                    width: '100%',
                    padding: 'var(--spacing-md) var(--spacing-md) var(--spacing-md) 40px',
                    border: `2px solid ${errors.name ? '#ef4444' : 'var(--colors-border-subtle)'}`,
                    borderRadius: 'var(--border-radius-lg)',
                    fontSize: 'var(--body-font-size)',
                    outline: 'none',
                    transition: 'border-color 0.2s ease'
                  }}
                />
              </div>
              {errors.name && (
                <p style={{ color: '#ef4444', fontSize: 'var(--metadata-font-size)', marginTop: 'var(--spacing-sm)' }}>
                  {errors.name}
                </p>
              )}
            </div>
          )}

          {/* Email */}
          <div>
            <label style={{ 
              display: 'block', 
              fontSize: 'var(--body-font-size)', 
              fontWeight: '600', 
              marginBottom: 'var(--spacing-sm)',
              color: 'var(--colors-text-primary)'
            }}>
              Email Address *
            </label>
            <div style={{ position: 'relative' }}>
              <Mail 
                size={20} 
                style={{
                  position: 'absolute',
                  left: 'var(--spacing-md)',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  color: 'var(--colors-text-secondary)'
                }} 
              />
              <input
                type="email"
                value={formData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                placeholder="Enter your email"
                style={{
                  width: '100%',
                  padding: 'var(--spacing-md) var(--spacing-md) var(--spacing-md) 40px',
                  border: `2px solid ${errors.email ? '#ef4444' : 'var(--colors-border-subtle)'}`,
                  borderRadius: 'var(--border-radius-lg)',
                  fontSize: 'var(--body-font-size)',
                  outline: 'none',
                  transition: 'border-color 0.2s ease'
                }}
              />
            </div>
            {errors.email && (
              <p style={{ color: '#ef4444', fontSize: 'var(--metadata-font-size)', marginTop: 'var(--spacing-sm)' }}>
                {errors.email}
              </p>
            )}
          </div>

          {/* Password */}
          <div>
            <label style={{ 
              display: 'block', 
              fontSize: 'var(--body-font-size)', 
              fontWeight: '600', 
              marginBottom: 'var(--spacing-sm)',
              color: 'var(--colors-text-primary)'
            }}>
              Password *
            </label>
            <div style={{ position: 'relative' }}>
              <Lock 
                size={20} 
                style={{
                  position: 'absolute',
                  left: 'var(--spacing-md)',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  color: 'var(--colors-text-secondary)'
                }} 
              />
              <input
                type={showPassword ? 'text' : 'password'}
                value={formData.password}
                onChange={(e) => handleInputChange('password', e.target.value)}
                placeholder="Enter your password"
                style={{
                  width: '100%',
                  padding: 'var(--spacing-md) var(--spacing-md) var(--spacing-md) 40px',
                  border: `2px solid ${errors.password ? '#ef4444' : 'var(--colors-border-subtle)'}`,
                  borderRadius: 'var(--border-radius-lg)',
                  fontSize: 'var(--body-font-size)',
                  outline: 'none',
                  transition: 'border-color 0.2s ease'
                }}
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                style={{
                  position: 'absolute',
                  right: 'var(--spacing-md)',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  color: 'var(--colors-text-secondary)'
                }}
              >
                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
              </button>
            </div>
            {errors.password && (
              <p style={{ color: '#ef4444', fontSize: 'var(--metadata-font-size)', marginTop: 'var(--spacing-sm)' }}>
                {errors.password}
              </p>
            )}
          </div>

          {/* Confirm Password (register only) */}
          {mode === 'register' && (
            <div>
              <label style={{ 
                display: 'block', 
                fontSize: 'var(--body-font-size)', 
                fontWeight: '600', 
                marginBottom: 'var(--spacing-sm)',
                color: 'var(--colors-text-primary)'
              }}>
                Confirm Password *
              </label>
              <input
                type="password"
                value={formData.confirmPassword}
                onChange={(e) => handleInputChange('confirmPassword', e.target.value)}
                placeholder="Confirm your password"
                style={{
                  width: '100%',
                  padding: 'var(--spacing-md)',
                  border: `2px solid ${errors.confirmPassword ? '#ef4444' : 'var(--colors-border-subtle)'}`,
                  borderRadius: 'var(--border-radius-lg)',
                  fontSize: 'var(--body-font-size)',
                  outline: 'none',
                  transition: 'border-color 0.2s ease'
                }}
              />
              {errors.confirmPassword && (
                <p style={{ color: '#ef4444', fontSize: 'var(--metadata-font-size)', marginTop: 'var(--spacing-sm)' }}>
                  {errors.confirmPassword}
                </p>
              )}
            </div>
          )}

          {/* Role-specific fields (register only) */}
          {mode === 'register' && role === 'freelancer' && (
            <>
              <div>
                <label style={{ 
                  display: 'block', 
                  fontSize: 'var(--body-font-size)', 
                  fontWeight: '600', 
                  marginBottom: 'var(--spacing-sm)',
                  color: 'var(--colors-text-primary)'
                }}>
                  University *
                </label>
                <input
                  type="text"
                  value={formData.university}
                  onChange={(e) => handleInputChange('university', e.target.value)}
                  placeholder="e.g., Stanford University"
                  style={{
                    width: '100%',
                    padding: 'var(--spacing-md)',
                    border: `2px solid ${errors.university ? '#ef4444' : 'var(--colors-border-subtle)'}`,
                    borderRadius: 'var(--border-radius-lg)',
                    fontSize: 'var(--body-font-size)',
                    outline: 'none',
                    transition: 'border-color 0.2s ease'
                  }}
                />
                {errors.university && (
                  <p style={{ color: '#ef4444', fontSize: 'var(--metadata-font-size)', marginTop: 'var(--spacing-sm)' }}>
                    {errors.university}
                  </p>
                )}
              </div>

              <div>
                <label style={{ 
                  display: 'block', 
                  fontSize: 'var(--body-font-size)', 
                  fontWeight: '600', 
                  marginBottom: 'var(--spacing-sm)',
                  color: 'var(--colors-text-primary)'
                }}>
                  Major/Field of Study *
                </label>
                <input
                  type="text"
                  value={formData.major}
                  onChange={(e) => handleInputChange('major', e.target.value)}
                  placeholder="e.g., Computer Science"
                  style={{
                    width: '100%',
                    padding: 'var(--spacing-md)',
                    border: `2px solid ${errors.major ? '#ef4444' : 'var(--colors-border-subtle)'}`,
                    borderRadius: 'var(--border-radius-lg)',
                    fontSize: 'var(--body-font-size)',
                    outline: 'none',
                    transition: 'border-color 0.2s ease'
                  }}
                />
                {errors.major && (
                  <p style={{ color: '#ef4444', fontSize: 'var(--metadata-font-size)', marginTop: 'var(--spacing-sm)' }}>
                    {errors.major}
                  </p>
                )}
              </div>
            </>
          )}

          {mode === 'register' && role === 'client' && (
            <>
              <div>
                <label style={{ 
                  display: 'block', 
                  fontSize: 'var(--body-font-size)', 
                  fontWeight: '600', 
                  marginBottom: 'var(--spacing-sm)',
                  color: 'var(--colors-text-primary)'
                }}>
                  Company/Organization Name *
                </label>
                <input
                  type="text"
                  value={formData.companyName}
                  onChange={(e) => handleInputChange('companyName', e.target.value)}
                  placeholder="e.g., Campus Coffee Co."
                  style={{
                    width: '100%',
                    padding: 'var(--spacing-md)',
                    border: `2px solid ${errors.companyName ? '#ef4444' : 'var(--colors-border-subtle)'}`,
                    borderRadius: 'var(--border-radius-lg)',
                    fontSize: 'var(--body-font-size)',
                    outline: 'none',
                    transition: 'border-color 0.2s ease'
                  }}
                />
                {errors.companyName && (
                  <p style={{ color: '#ef4444', fontSize: 'var(--metadata-font-size)', marginTop: 'var(--spacing-sm)' }}>
                    {errors.companyName}
                  </p>
                )}
              </div>

              <div>
                <label style={{ 
                  display: 'block', 
                  fontSize: 'var(--body-font-size)', 
                  fontWeight: '600', 
                  marginBottom: 'var(--spacing-sm)',
                  color: 'var(--colors-text-primary)'
                }}>
                  Phone Number *
                </label>
                <input
                  type="tel"
                  value={formData.phone}
                  onChange={(e) => handleInputChange('phone', e.target.value)}
                  placeholder="e.g., (555) 123-4567"
                  style={{
                    width: '100%',
                    padding: 'var(--spacing-md)',
                    border: `2px solid ${errors.phone ? '#ef4444' : 'var(--colors-border-subtle)'}`,
                    borderRadius: 'var(--border-radius-lg)',
                    fontSize: 'var(--body-font-size)',
                    outline: 'none',
                    transition: 'border-color 0.2s ease'
                  }}
                />
                {errors.phone && (
                  <p style={{ color: '#ef4444', fontSize: 'var(--metadata-font-size)', marginTop: 'var(--spacing-sm)' }}>
                    {errors.phone}
                  </p>
                )}
              </div>
            </>
          )}

          {/* Submit Button */}
          <button 
            type="submit"
            className="button button-primary"
            style={{ width: '100%', fontSize: 'var(--body-font-size)', padding: 'var(--spacing-md)' }}
          >
            {mode === 'login' ? 'Sign In' : 'Create Account'}
          </button>

          {/* Terms (register only) */}
          {mode === 'register' && (
            <p style={{ 
              fontSize: 'var(--metadata-font-size)', 
              color: 'var(--colors-text-secondary)', 
              textAlign: 'center',
              lineHeight: '1.5'
            }}>
              By creating an account, you agree to our{' '}
              <a href="/terms" style={{ color: 'var(--colors-primary-limeText)', textDecoration: 'none' }}>
                Terms of Service
              </a>{' '}
              and{' '}
              <a href="/privacy" style={{ color: 'var(--colors-primary-limeText)', textDecoration: 'none' }}>
                Privacy Policy
              </a>
            </p>
          )}
        </div>
      </form>
    </Modal>
  );
};

export default AuthModal;
