import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import LandingPage from './components/LandingPage';
import Dashboard from './components/Dashboard';
import ClientDashboard from './components/ClientDashboard';
import ProjectMarketplace from './components/ProjectMarketplace';
import Chat from './components/Chat';
import Portfolio from './components/Portfolio';
import PaymentEscrow from './components/PaymentEscrow';
import AuthModal from './components/AuthModal';
import './index.css';

function App() {
  const [user, setUser] = useState({
    id: 1,
    name: 'Alex Chen',
    role: 'freelancer', // 'freelancer' or 'client'
    university: 'Stanford University',
    avatar: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=40&h=40&fit=crop&crop=face',
    reputation: 4.8,
    completedProjects: 12,
    totalEarnings: 2450
  });
  
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(true); // Set to false for landing page demo

  const handleAuth = (authData) => {
    console.log('Authentication data:', authData);
    // In a real app, this would handle authentication
    setIsLoggedIn(true);
    setIsAuthModalOpen(false);
  };

  if (!isLoggedIn) {
    return (
      <Router>
        <div className="App">
          <LandingPage />
          <Footer />
          <AuthModal 
            isOpen={isAuthModalOpen}
            onClose={() => setIsAuthModalOpen(false)}
            onAuth={handleAuth}
          />
        </div>
      </Router>
    );
  }

  return (
    <Router>
      <div className="App">
        <Header user={user} />
        <main style={{ paddingTop: '80px', minHeight: 'calc(100vh - 160px)' }}>
          <Routes>
            <Route path="/" element={
              user.role === 'freelancer' ? 
                <Dashboard user={user} /> : 
                <ClientDashboard user={user} />
            } />
            <Route path="/marketplace" element={<ProjectMarketplace user={user} />} />
            <Route path="/chat" element={<Chat user={user} />} />
            <Route path="/portfolio" element={<Portfolio user={user} />} />
            <Route path="/payments" element={<PaymentEscrow user={user} />} />
            <Route path="/client-dashboard" element={<ClientDashboard user={user} />} />
            <Route path="/post-project" element={<ProjectMarketplace user={user} />} />
          </Routes>
        </main>
        <Footer />
        <AuthModal 
          isOpen={isAuthModalOpen}
          onClose={() => setIsAuthModalOpen(false)}
          onAuth={handleAuth}
        />
      </div>
    </Router>
  );
}

export default App;
